
public class Address {
	String streetName;
	String city;
	String zip;
	String aptNumber;
	String state;
	String country;

	public Address(String streetName,String aptNumber,String city,String zip,String state,String country) 
	{
		// TODO Auto-generated constructor stub
	this.streetName = streetName;
	this.city = city;
	this.zip = zip;
	this.aptNumber = aptNumber;
	this.state = state;
	this.country = country;
	}
	

}
